mapper.select:
SELECT E.END_CNT AS END_CNT, I.ING_CNT AS ING_CNT FROM (SELECT COUNT(*) AS END_CNT FROM ( SELECT P.PRO_NO FROM PROJECT P JOIN EMPLOYEE E ON P.EMP_ID = E.EMP_ID JOIN TEAM T ON E.TEAM_CD = T.TEAM_CD WHERE T.TEAM_CD = #{teamCd}
AND P.PRO_END_DT < SYSDATE
) ) E, (SELECT COUNT(*) AS ING_CNT FROM ( SELECT P.PRO_NO FROM PROJECT P JOIN EMPLOYEE E ON P.EMP_ID = E.EMP_ID JOIN TEAM T ON E.TEAM_CD = T.TEAM_CD WHERE T.TEAM_CD = #{teamCd} ) ) I

SELECT EMP_ID
FROM EMPLOYEE
WHERE EMP_ID = #{empId}

mapper.update:
		UPDATE DEPARTMENT
  		SET
	  		DEPT_NM = #{deptNm}
  		WHERE DEPT_CD = #{deptCd}

mapper.select:
SELECT b.* FROM ( SELECT a.*, row_number() over () rnum FROM ( SELECT E.EMP_ID AS EMP_ID, E.CO_CD, D.DEPT_NM AS DEPT_CD, E.JBGD_CD AS EJBGD_CD, J.JBGD_NM AS JBGD_CD, T.TEAM_NM AS TEAM_CD, E.EMP_PW, E.EMP_EML_ADDR, E.EMP_NM AS EMP_NM, E.EMP_NO, E.EMP_ZIP, E.EMP_ADDR, E.EMP_DADDR, E.EMP_TELNO, E.EMP_BRDT, E.EMP_GNDR, E.EMP_NTN, E.EMP_JNCMP_YMD, E.EMP_RSGNTN_YMD, E.EMP_CRT_YMD, E.EMP_MDFCN_YMD, E.EMP_DEL_YN, E.EMP_APRV_YN, E.ENABLED, E.EMP_DLN_YN, E.IMG_FILE_URL FROM EMPLOYEE E, DEPARTMENT D, JOBGRADE J, TEAM T WHERE E.CO_CD = #{coCd} AND E.DEPT_CD = D.DEPT_CD AND E.JBGD_CD = J.JBGD_CD AND E.TEAM_CD = T.TEAM_CD
) a ) b
WHERE b.rnum >= #{startRow} and b.rnum <= #{endRow}

SELECT MENU_NO, MENU_NM, MENU_ICON, MENU_PREMIUM
FROM MENU
WHERE MENU_NO = #{menuNo}

SELECT team_cd, team_nm
FROM team
WHERE dept_cd = #{deptCd}

SELECT TEAM_CD, TEAM_NM
FROM TEAM
WHERE DEPT_CD = #{deptCd}

	SELECT JBGD_CD, JBGD_NM 
    FROM JOBGRADE
    WHERE CO_CD = #{coCd}

