mapper.insert:
insert into video_chatting(
chat_no, video_chat_nm, video_chat_date, video_chat_mas, video_chat_url, video_chat_pw, video_chat_delyn, password_required, video_chat_com, video_chat_id
)values(
	seq_chat_no.nextval, #{videoChatNm}, #{videoChatDate}, #{videoChatMas}, #{videoChatUrl}, #{videoChatPw}, 'N', #{passwordRequired}, #{videoChatCom}, #{videoChatId}
)

mapper.select:
SELECT b.*, c.co_nm FROM ( SELECT a.*, ROW_NUMBER() OVER (ORDER BY a.chat_no ASC) AS rnum FROM ( SELECT chat_no, video_chat_nm, video_chat_mas, video_chat_date, video_chat_pw, video_chat_id, password_required, video_chat_com FROM video_chatting WHERE 1=1
AND video_chat_delyn = 'N' ORDER BY chat_no ASC ) a ) b JOIN company c ON b.video_chat_com = c.co_cd
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.update:
update video_chatting
set video_chat_delyn = 'Y'
where chat_no = #{id}
