mapper.insert:
INSERT INTO FACILITY (
	FCLT_CD, FCLT_CTGR_CD, FCLT_NM, FCLT_START_TM, FCLT_END_TM, FCLT_NOPE, ATCH_FILE_CD, FCLT_INFO
) VALUES(
	#{fcltCd}, #{fcltCtgrCd}, #{fcltNm}, #{fcltStartTm}, #{fcltEndTm}, #{fcltNope}, #{atchFileCd}, #{fcltInfo}
)

mapper.select:
SELECT FCLT_RSVT_DSCTN_CD, MEM_ID, FCLT_CD, TO_CHAR(RSVT_YMD,'yyyy-MM-dd') RSVT_YMD, RSVT_BGNG_TM, RSVT_END_TM FROM FACILITY_RESERVATION_DETAIL WHERE TO_CHAR(RSVT_YMD,'yyyy-MM-dd') = #{rsvtYmd} AND FCLT_CD = #{fcltCd} AND
RSVT_BGNG_TM > #{rsvtBgngTm}
AND ROWNUM = 1

  	select
		b.*
	from (
		select 
			a.*, row_number() over (order by a.FCLT_RSVT_DSCTN_CD desc) rnum 
		from (
			select
				FCLT_RSVT_DSCTN_CD,
				MEM_ID, 
				FCLT_CD, 
				TO_CHAR(RSVT_YMD,'yyyy-MM-dd') RSVT_YMD, 
				RSVT_BGNG_TM, 
				RSVT_END_TM
			from FACILITY_RESERVATION_DETAIL
			where MEM_ID = #{empId}
			order by FCLT_RSVT_DSCTN_CD desc
		) a
	) b

		where b.rnum >= #{startRow} and b.rnum <= #{endRow}
