mapper.select:
select b.* from (SELECT row_number() over (order by CMNTY_NOTICE_YN desc, cmnty_reg_dt desc) rnum, CMNTY_NO , EMP_ID , CO_CD , ATCH_FILE_CD , CMNTY_TYPE , CMNTY_TTL , CMNTY_REG_DT , CMNTY_MDFCN_DT , CMNTY_CN , CMNTY_DEL_YN , CMNTY_INQ_CNT, CMNTY_NOTICE_YN FROM COMMUNITY where CMNTY_TYPE = 'GroNotice' and CMNTY_DEL_YN = 'N'
order by CMNTY_NOTICE_YN desc, cmnty_reg_dt desc) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:

	select seq_community.nextval from dual

