mapper.update:
UPDATE ATCH_FILE_DETAIL
SET    ATCH_FILE_DETAIL_DEL_YN = 'Y'
WHERE  ATCH_FILE_CD = #{atchFileCd}
AND    ATCH_FILE_DETAIL_CD NOT IN

 	#{atchFileDetailVO.atchFileDetailCd}

UPDATE community
SET cmnty_del_yn = 'Y'
WHERE cmnty_no = #{cmntyNo}

UPDATE ATCH_FILE_DETAIL
SET ATCH_FILE_DETAIL_DEL_YN = 'Y'
WHERE ATCH_FILE_CD = #{atchFileCd}

UPDATE community
SET CMNTY_INQ_CNT = CMNTY_INQ_CNT + 1
WHERE cmnty_no = #{cmntyNo}

mapper.select:
select b.* from (SELECT row_number() over (order by cmnty_reg_dt desc) rnum, C.CMNTY_NO CMNTY_NO, C.EMP_ID QSTN_EMP_ID, C.CO_CD CO_CD, C.CMNTY_TYPE CMNTY_TYPE, C.CMNTY_TTL CMNTY_TTL, C.CMNTY_REG_DT CMNTY_REG_DT, C.CMNTY_MDFCN_DT CMNTY_MDFCN_DT, C.CMNTY_CN CMNTY_CN, C.CMNTY_DEL_YN CMNTY_DEL_YN, C.CMNTY_INQ_CNT CMNTY_INQ_CNT, C.COM_DTL_CD COM_DTL_CD, C.CMNTY_QSTN_STTS_YN CMNTY_QSTN_STTS_YN, C.CMNTY_PRVT_PST_YN CMNTY_PRVT_PST_YN, D.COM_CD COM_CD, D.COM_DTL_CD_NM COM_DTL_CD_NM FROM COMMUNITY C inner join COMMON_DETAIL_CODE D on (c.com_dtl_cd = d.com_dtl_cd) where CMNTY_TYPE = 'Qstn' and CMNTY_DEL_YN = 'N'
and c.COM_DTL_CD = #{cmntyType}
and c.CMNTY_QSTN_STTS_YN = 'N'
order by cmnty_reg_dt desc) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:

